INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (2, 2, 'Scientists made a biodegradable robot from plants and animals.', '科学家们用植物和动物制造了一个可生物降解的机器人。', '这是一个简单句，主语是 Scientists，谓语是 made，宾语是 a biodegradable robot，from plants and animals 是材料状语。', '{"components": [{"text": "Scientists", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "made", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "a biodegradable robot", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "from plants and animals", "label": "\u6750\u6599\u72b6\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["Scientists", "made", "a biodegradable robot"], "modifier": ["from plants and animals"]}', NULL, NULL, 'null', '2025-12-18 08:34:16.593705');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (3, 1, 'However, this same quality is now causing a big problem—it''s creating tons of long-lasting e-waste that could flood our planet.', '然而，这一相同的特质现在正在引发一个大问题——它正在产生大量持久性的电子垃圾，这些垃圾可能会淹没我们的星球。', '这是一个复合句，主句是 this same quality is now causing a big problem。However 是连接词，引导转折。it''s creating tons of long-lasting e-waste 是一个并列分句，解释主句的问题。that could flood our planet 是定语从句，修饰 e-waste。', '{"components": [{"text": "However", "label": "\u8fde\u63a5\u8bcd", "type": "unknown", "deletable": false}, {"text": "this same quality", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "is now causing", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "a big problem", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "it''s creating tons of long-lasting e-waste", "label": "\u5e76\u5217\u5206\u53e5", "type": "unknown", "deletable": false}, {"text": "that could flood our planet", "label": "\u5b9a\u8bed\u4ece\u53e5", "type": "unknown", "deletable": false}]}', '{"core": ["this same quality", "is now causing", "a big problem"], "modifier": ["However", "it''s creating tons of long-lasting e-waste", "that could flood our planet"]}', NULL, NULL, 'null', '2025-12-18 08:39:24.709801');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (4, 1, 'For a study published in Science Advances, researchers made a robotic arm and a controller using materials from animals and plants.', '为了发表在《科学进展》上的一项研究，研究人员用动植物材料制作了一个机械臂和一个控制器。', '主句是 researchers made a robotic arm and a controller。For a study published in Science Advances 是目的状语，using materials from animals and plants 是方式状语。', '{"components": [{"text": "For a study published in Science Advances", "label": "\u76ee\u7684\u72b6\u8bed", "type": "unknown", "deletable": false}, {"text": "researchers", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "made", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "a robotic arm and a controller", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "using materials from animals and plants", "label": "\u65b9\u5f0f\u72b6\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["researchers", "made", "a robotic arm and a controller"], "modifier": ["For a study published in Science Advances", "using materials from animals and plants"]}', NULL, NULL, 'null', '2025-12-18 08:39:24.718065');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (5, 1, 'Wei and Zhang expect that robots like these can be used to deal with dangerous waste and then disappear naturally.', '魏和张期望这些机器人可以被用来处理危险废物，然后自然消失。', '主句是 Wei and Zhang expect that...。that robots like these can be used to deal with dangerous waste and then disappear naturally 是宾语从句，修饰 expect。在这个宾语从句中，robots like these 是主语，can be used 是谓语，to deal with dangerous waste and then disappear naturally 是状语。', '{"components": [{"text": "Wei and Zhang", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "expect", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "that robots like these can be used to deal with dangerous waste and then disappear naturally", "label": "\u5bbe\u8bed\u4ece\u53e5", "type": "unknown", "deletable": false}, {"text": "robots like these", "label": "\u5bbe\u8bed\u4ece\u53e5\u4e2d\u7684\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "can be used", "label": "\u5bbe\u8bed\u4ece\u53e5\u4e2d\u7684\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "to deal with dangerous waste and then disappear naturally", "label": "\u5bbe\u8bed\u4ece\u53e5\u4e2d\u7684\u72b6\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["Wei and Zhang", "expect", "that robots like these can be used to deal with dangerous waste and then disappear naturally", "robots like these", "can be used", "to deal with dangerous waste and then disappear naturally"], "modifier": []}', NULL, NULL, 'null', '2025-12-18 08:39:24.720044');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (1, 2, 'Robots can be very strong, but they make a lot of e-waste.', '机器人可以非常强大，但它们会产生很多电子垃圾。', '这是一个复合句，由两个分句组成。第一个分句的主语是 Robots，谓语是 can be，表语是 very strong；第二个分句的主语是 they，谓语是 make，宾语是 a lot of e-waste，but 是连接词。', '{"components": [{"text": "Robots", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "can be very strong", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "but", "label": "\u8fde\u63a5\u8bcd", "type": "unknown", "deletable": false}, {"text": "they", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "make", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "a lot of e-waste", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["Robots", "can be very strong", "they", "make", "a lot of e-waste"], "modifier": ["but"]}', NULL, NULL, 'null', '2025-12-18 08:34:16.577454');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (19, 7, 'However, this creates a big problem.', '然而，这造成了一个大问题。', '这是一个复合句，主句是 this creates a big problem，However 是连接词，表示转折。', '{"components": [{"text": "However", "label": "\u8fde\u63a5\u8bcd", "type": "unknown", "deletable": false}, {"text": "this", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "creates", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "a big problem", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["this", "creates", "a big problem"], "modifier": ["However"]}', NULL, NULL, 'null', '2025-12-18 08:41:14.630574');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (20, 7, 'They used materials from nature.', '他们使用了来自自然的材料。', '这是一个简单句，主语是 They，谓语是 used，宾语是 materials from nature。', '{"components": [{"text": "They", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "used", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "materials from nature", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["They", "used", "materials from nature"], "modifier": []}', NULL, NULL, 'null', '2025-12-18 08:41:14.634507');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (6, 3, 'But this strength causes a big problem—lots of e-waste that harms our planet.', '但这种力量造成了一个大问题——大量损害我们星球的电子垃圾。', '这是一个复合句，主句是 this strength causes a big problem，破折号后是解释说明的部分，其中 lots of e-waste 是宾语，that harms our planet 是定语从句。', '{"components": [{"text": "But", "label": "\u8fde\u63a5\u8bcd", "type": "unknown", "deletable": false}, {"text": "this strength", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "causes", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "a big problem", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "lots of e-waste", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "that harms our planet", "label": "\u5b9a\u8bed\u4ece\u53e5", "type": "unknown", "deletable": false}]}', '{"core": ["this strength", "causes", "a big problem", "lots of e-waste"], "modifier": ["But", "that harms our planet"]}', NULL, NULL, 'null', '2025-12-18 08:39:51.641637');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (7, 3, 'After testing, they disappeared in soil within weeks.', '经过测试后，它们在几周内消失在土壤中。', '这是一个复合句，主句是 they disappeared in soil within weeks，After testing 是时间状语。', '{"components": [{"text": "After testing", "label": "\u65f6\u95f4\u72b6\u8bed", "type": "unknown", "deletable": false}, {"text": "they", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "disappeared", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "in soil within weeks", "label": "\u5730\u70b9\u72b6\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["they", "disappeared"], "modifier": ["After testing", "in soil within weeks"]}', NULL, NULL, 'null', '2025-12-18 08:39:51.647737');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (8, 4, 'In a study in Science Advances, researchers made a robotic arm and controller from animal and plant materials.', '在《科学进展》的一项研究中，研究人员用动植物材料制作了一个机械臂和控制器。', '这是一个复合句，主句是 researchers made a robotic arm and controller。In a study in Science Advances 是介词短语作状语，from animal and plant materials 是介词短语作定语，修饰 robotic arm and controller。', '{"components": [{"text": "In a study in Science Advances", "label": "\u72b6\u8bed", "type": "unknown", "deletable": false}, {"text": "researchers", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "made", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "a robotic arm and controller", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "from animal and plant materials", "label": "\u5b9a\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["researchers", "made", "a robotic arm and controller"], "modifier": ["In a study in Science Advances", "from animal and plant materials"]}', NULL, NULL, 'null', '2025-12-18 08:40:07.735799');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (9, 4, 'These materials are strong but can break down easily in nature.', '这些材料很坚固，但在自然中容易分解。', '这是一个复合句，主句是 These materials are strong，but 是连接词，引导并列句 can break down easily in nature。', '{"components": [{"text": "These materials", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "are strong", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "but", "label": "\u8fde\u63a5\u8bcd", "type": "unknown", "deletable": false}, {"text": "can break down", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "easily", "label": "\u65b9\u5f0f\u72b6\u8bed", "type": "unknown", "deletable": false}, {"text": "in nature", "label": "\u5730\u70b9\u72b6\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["These materials", "are strong", "can break down"], "modifier": ["but", "easily", "in nature"]}', NULL, NULL, 'null', '2025-12-18 08:40:07.741109');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (10, 4, 'Wei and Zhang hope these robots can handle dangerous waste and then disappear.', '魏和张希望这些机器人能够处理危险废物，然后消失。', '主句是 Wei and Zhang hope。these robots can handle dangerous waste and then disappear 是宾语从句，作为 hope 的宾语。', '{"components": [{"text": "Wei and Zhang", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "hope", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "these robots", "label": "\u5bbe\u8bed\u4ece\u53e5\u4e2d\u7684\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "can handle", "label": "\u5bbe\u8bed\u4ece\u53e5\u4e2d\u7684\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "dangerous waste", "label": "\u5bbe\u8bed\u4ece\u53e5\u4e2d\u7684\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "and then disappear", "label": "\u5bbe\u8bed\u4ece\u53e5\u4e2d\u7684\u5e76\u5217\u8c13\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["Wei and Zhang", "hope", "these robots", "can handle", "dangerous waste", "and then disappear"], "modifier": []}', NULL, NULL, 'null', '2025-12-18 08:40:07.743547');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (11, 5, 'However, this same quality is now causing a big problem—it''s creating tons of long-lasting e-waste that could flood our planet.', '然而，这种相同的品质现在正在引发一个大问题——它正在制造大量长期存在的电子垃圾，这些垃圾可能会淹没我们的星球。', '这是一个复合句，主句是 this same quality is now causing a big problem。However 是连接词，引导转折关系。it''s creating tons of long-lasting e-waste 是并列句，解释大问题的具体内容。that could flood our planet 是定语从句，修饰 e-waste。', '{"components": [{"text": "However", "label": "\u8fde\u63a5\u8bcd", "type": "unknown", "deletable": false}, {"text": "this same quality", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "is now causing", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "a big problem", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "it''s creating tons of long-lasting e-waste", "label": "\u5e76\u5217\u53e5", "type": "unknown", "deletable": false}, {"text": "that could flood our planet", "label": "\u5b9a\u8bed\u4ece\u53e5", "type": "unknown", "deletable": false}]}', '{"core": ["this same quality", "is now causing", "a big problem"], "modifier": ["However", "it''s creating tons of long-lasting e-waste", "that could flood our planet"]}', NULL, NULL, 'null', '2025-12-18 08:40:26.311333');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (12, 5, 'For a study published in Science Advances, researchers made a robotic arm and a controller using materials from animals and plants.', '为了发表在《科学进展》上的一项研究，研究人员用动植物材料制作了一个机械臂和一个控制器。', '主句是 researchers made a robotic arm and a controller。For a study published in Science Advances 是目的状语，using materials from animals and plants 是方式状语。', '{"components": [{"text": "For a study published in Science Advances", "label": "\u76ee\u7684\u72b6\u8bed", "type": "unknown", "deletable": false}, {"text": "researchers", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "made", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "a robotic arm and a controller", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "using materials from animals and plants", "label": "\u65b9\u5f0f\u72b6\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["researchers", "made", "a robotic arm and a controller"], "modifier": ["For a study published in Science Advances", "using materials from animals and plants"]}', NULL, NULL, 'null', '2025-12-18 08:40:26.315315');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (13, 5, 'Wei and Zhang expect that robots like these can be used to deal with dangerous waste and then disappear naturally.', '魏和张期望像这样的机器人可以用来处理危险废物，然后自然消失。', '主句是 Wei and Zhang expect that...。that 引导的宾语从句是 robots like these can be used to deal with dangerous waste and then disappear naturally，其中 like these 是定语，修饰 robots。', '{"components": [{"text": "Wei and Zhang", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "expect", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "that robots like these can be used to deal with dangerous waste and then disappear naturally", "label": "\u5bbe\u8bed\u4ece\u53e5", "type": "unknown", "deletable": false}, {"text": "robots", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "like these", "label": "\u5b9a\u8bed", "type": "unknown", "deletable": false}, {"text": "can be used to deal with dangerous waste", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "and then disappear naturally", "label": "\u5e76\u5217\u8c13\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["Wei and Zhang", "expect", "that robots like these can be used to deal with dangerous waste and then disappear naturally", "robots", "can be used to deal with dangerous waste", "and then disappear naturally"], "modifier": ["like these"]}', NULL, NULL, 'null', '2025-12-18 08:40:26.317161');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (14, 8, 'Traditional robots create massive amounts of electronic waste, known as e-waste, which can flood our planet for centuries.', '传统机器人制造了大量的电子垃圾，被称为电子废物，这些废物可能会淹没我们的星球数百年。', '主句是 Traditional robots create massive amounts of electronic waste。known as e-waste 是非限制性定语从句，修饰 electronic waste。which can flood our planet for centuries 是另一个非限制性定语从句，修饰 e-waste。', '{"components": [{"text": "Traditional robots", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "create", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "massive amounts of electronic waste", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "known as e-waste", "label": "\u975e\u9650\u5236\u6027\u5b9a\u8bed\u4ece\u53e5", "type": "unknown", "deletable": false}, {"text": "which can flood our planet for centuries", "label": "\u975e\u9650\u5236\u6027\u5b9a\u8bed\u4ece\u53e5", "type": "unknown", "deletable": false}]}', '{"core": ["Traditional robots", "create", "massive amounts of electronic waste"], "modifier": ["known as e-waste", "which can flood our planet for centuries"]}', NULL, NULL, 'null', '2025-12-18 08:40:45.077703');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (15, 8, 'In a study published in *Science Advances*, researchers created a robotic arm and a controller using materials derived from plants and animals, which are strong enough to perform tasks, yet they can easily decompose in a natural environment.', '在一项发表在《科学进展》的研究中，研究人员使用来自植物和动物的材料制造了一个机械臂和一个控制器，这些材料足够强大，可以执行任务，但它们在自然环境中可以轻易分解。', '主句是 researchers created a robotic arm and a controller。In a study published in *Science Advances* 是地点状语，using materials derived from plants and animals 是方式状语，which are strong enough to perform tasks, yet they can easily decompose in a natural environment 是非限制性定语从句，修饰 materials。', '{"components": [{"text": "In a study published in *Science Advances*", "label": "\u5730\u70b9\u72b6\u8bed", "type": "unknown", "deletable": false}, {"text": "researchers", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "created", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "a robotic arm and a controller", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "using materials derived from plants and animals", "label": "\u65b9\u5f0f\u72b6\u8bed", "type": "unknown", "deletable": false}, {"text": "which are strong enough to perform tasks, yet they can easily decompose in a natural environment", "label": "\u975e\u9650\u5236\u6027\u5b9a\u8bed\u4ece\u53e5", "type": "unknown", "deletable": false}]}', '{"core": ["researchers", "created", "a robotic arm and a controller"], "modifier": ["In a study published in *Science Advances*", "using materials derived from plants and animals", "which are strong enough to perform tasks, yet they can easily decompose in a natural environment"]}', NULL, NULL, 'null', '2025-12-18 08:40:45.080437');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (16, 8, 'Wei and Zhang hope that robots like these can be used to manage dangerous waste and then disappear naturally, which would also be useful for helping doctors during surgeries and then safely breaking down inside the human body.', '魏和张希望像这样的机器人可以用来管理危险废物，然后自然消失，这也将有助于在手术中帮助医生，然后安全地在人体内分解。', '这是一个复合句，主句是 Wei and Zhang hope that...，其中 that 引导的是一个宾语从句。宾语从句中包含两个并列的谓语部分，分别是 can be used to manage dangerous waste and then disappear naturally 和 would also be useful for helping doctors during surgeries and then safely breaking down inside the human body。which 引导的是一个非限制性定语从句，修饰整个宾语从句。', '{"components": [{"text": "Wei and Zhang", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "hope", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "that robots like these can be used to manage dangerous waste and then disappear naturally", "label": "\u5bbe\u8bed\u4ece\u53e5", "type": "unknown", "deletable": false}, {"text": "which would also be useful for helping doctors during surgeries and then safely breaking down inside the human body", "label": "\u5b9a\u8bed\u4ece\u53e5", "type": "unknown", "deletable": false}, {"text": "robots like these", "label": "\u5bbe\u8bed\u4ece\u53e5\u4e2d\u7684\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "can be used to manage dangerous waste", "label": "\u5bbe\u8bed\u4ece\u53e5\u4e2d\u7684\u8c13\u8bed\u90e8\u5206", "type": "unknown", "deletable": false}, {"text": "and then disappear naturally", "label": "\u5bbe\u8bed\u4ece\u53e5\u4e2d\u7684\u5e76\u5217\u8c13\u8bed\u90e8\u5206", "type": "unknown", "deletable": false}, {"text": "would also be useful for helping doctors during surgeries", "label": "\u5b9a\u8bed\u4ece\u53e5\u4e2d\u7684\u8c13\u8bed\u90e8\u5206", "type": "unknown", "deletable": false}, {"text": "and then safely breaking down inside the human body", "label": "\u5b9a\u8bed\u4ece\u53e5\u4e2d\u7684\u5e76\u5217\u8c13\u8bed\u90e8\u5206", "type": "unknown", "deletable": false}]}', '{"core": ["Wei and Zhang", "hope", "that robots like these can be used to manage dangerous waste and then disappear naturally", "robots like these", "can be used to manage dangerous waste", "and then disappear naturally", "would also be useful for helping doctors during surgeries", "and then safely breaking down inside the human body"], "modifier": ["which would also be useful for helping doctors during surgeries and then safely breaking down inside the human body"]}', NULL, NULL, 'null', '2025-12-18 08:40:45.082165');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (17, 6, 'Robots are usually made of metal.', '机器人通常是由金属制成的。', '这是一个简单句，主语是 Robots，谓语是 are made，usually 是频率状语，of metal 是材料状语。', '{"components": [{"text": "Robots", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "are usually made", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "of metal", "label": "\u6750\u6599\u72b6\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["Robots", "are usually made"], "modifier": ["of metal"]}', NULL, NULL, 'null', '2025-12-18 08:40:59.166931');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (18, 6, 'They use plants to make robot parts.', '他们用植物制作机器人部件。', '这是一个简单句，主语是 They，谓语是 use，宾语是 plants，to make robot parts 是目的状语。', '{"components": [{"text": "They", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "use", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "plants", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "to make robot parts", "label": "\u76ee\u7684\u72b6\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["They", "use", "plants"], "modifier": ["to make robot parts"]}', NULL, NULL, 'null', '2025-12-18 08:40:59.170703');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (21, 12, 'However, this same quality is now causing a big problem—it''s creating tons of long-lasting e-waste that could flood our planet.', '然而，这种相同的特质现在正在引发一个大问题——它正在产生大量持久性的电子垃圾，这些垃圾可能会淹没我们的星球。', '这是一个复合句，主句是 this same quality is now causing a big problem，其中 However 是连接词，this same quality 是主语，is causing 是谓语，a big problem 是宾语。it''s creating tons of long-lasting e-waste that could flood our planet 是一个解释性的从句，其中 it''s creating 是谓语，tons of long-lasting e-waste 是宾语，that could flood our planet 是定语从句，修饰 e-waste。', '{"components": [{"text": "However", "label": "\u8fde\u63a5\u8bcd", "type": "unknown", "deletable": false}, {"text": "this same quality", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "is now causing", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "a big problem", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "it''s creating tons of long-lasting e-waste", "label": "\u8c13\u8bed\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "that could flood our planet", "label": "\u5b9a\u8bed\u4ece\u53e5", "type": "unknown", "deletable": false}]}', '{"core": ["this same quality", "is now causing", "a big problem", "it''s creating tons of long-lasting e-waste"], "modifier": ["However", "that could flood our planet"]}', NULL, NULL, 'null', '2025-12-18 08:41:35.466238');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (22, 12, 'For a study published in Science Advances, researchers made a robotic arm and a controller using materials from animals and plants.', '为了发表在《科学进展》上的一项研究，研究人员使用动植物材料制作了一个机械臂和一个控制器。', '主句是 researchers made a robotic arm and a controller。For a study published in Science Advances 是目的状语，using materials from animals and plants 是方式状语。', '{"components": [{"text": "For a study published in Science Advances", "label": "\u76ee\u7684\u72b6\u8bed", "type": "unknown", "deletable": false}, {"text": "researchers", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "made", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "a robotic arm and a controller", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "using materials from animals and plants", "label": "\u65b9\u5f0f\u72b6\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["researchers", "made", "a robotic arm and a controller"], "modifier": ["For a study published in Science Advances", "using materials from animals and plants"]}', NULL, NULL, 'null', '2025-12-18 08:41:35.47102');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (23, 12, 'Wei and Zhang expect that robots like these can be used to deal with dangerous waste and then disappear naturally.', '魏和张期望这些类似的机器人可以用来处理危险废物，然后自然消失。', '主句是 Wei and Zhang expect that...，其中 that 引导的是宾语从句。宾语从句中的主语是 robots，like these 是定语，修饰 robots；can be used to deal with dangerous waste 是谓语部分，and then disappear naturally 是并列的谓语部分。', '{"components": [{"text": "Wei and Zhang", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "expect", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "that robots like these can be used to deal with dangerous waste and then disappear naturally", "label": "\u5bbe\u8bed\u4ece\u53e5", "type": "unknown", "deletable": false}, {"text": "robots", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "like these", "label": "\u5b9a\u8bed", "type": "unknown", "deletable": false}, {"text": "can be used to deal with dangerous waste", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "and then disappear naturally", "label": "\u5e76\u5217\u8c13\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["Wei and Zhang", "expect", "that robots like these can be used to deal with dangerous waste and then disappear naturally", "robots", "can be used to deal with dangerous waste", "and then disappear naturally"], "modifier": ["like these"]}', NULL, NULL, 'null', '2025-12-18 08:41:35.473014');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (24, 14, 'Most robots are strong, but they create a big problem.', '大多数机器人很强壮，但它们制造了一个大问题。', '这是一个由两个简单句组成的复合句。第一个句子的主语是 Most robots，谓语是 are，表语是 strong。第二个句子的主语是 they，谓语是 create，宾语是 a big problem。but 是连接词，连接两个句子。', '{"components": [{"text": "Most robots", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "are strong", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "but", "label": "\u8fde\u63a5\u8bcd", "type": "unknown", "deletable": false}, {"text": "they", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "create a big problem", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["Most robots", "are strong", "they", "create a big problem"], "modifier": ["but"]}', NULL, NULL, 'null', '2025-12-18 08:41:44.352885');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (25, 14, 'When they break, they become electronic waste.', '当它们损坏时，它们就变成了电子垃圾。', '这是一个复合句，主句是 they become electronic waste，When they break 是时间状语从句。', '{"components": [{"text": "When they break", "label": "\u65f6\u95f4\u72b6\u8bed\u4ece\u53e5", "type": "unknown", "deletable": false}, {"text": "they", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "become", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "electronic waste", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["they", "become", "electronic waste"], "modifier": ["When they break"]}', NULL, NULL, 'null', '2025-12-18 08:41:44.355355');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (26, 15, 'When people imagine a robot, they usually think of a powerful machine that is stronger than a human body.', '当人们想象机器人时，他们通常想到的是比人体更强大的机器。', '主句是 they usually think of a powerful machine。When people imagine a robot 是时间状语从句，修饰主句。that is stronger than a human body 是定语从句，修饰宾语 a powerful machine。', '{"components": [{"text": "When people imagine a robot", "label": "\u65f6\u95f4\u72b6\u8bed\u4ece\u53e5", "type": "unknown", "deletable": false}, {"text": "they", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "usually think of", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "a powerful machine", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "that is stronger than a human body", "label": "\u5b9a\u8bed\u4ece\u53e5", "type": "unknown", "deletable": false}]}', '{"core": ["they", "usually think of", "a powerful machine"], "modifier": ["When people imagine a robot", "that is stronger than a human body"]}', NULL, NULL, 'null', '2025-12-18 08:42:03.074305');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (27, 15, 'Traditional robots produce large amounts of electronic waste, or ''e-waste,'' which stays in the environment for a long time.', '传统机器人产生大量电子垃圾，或称‘电子废物’，这些废物在环境中停留很长时间。', '主句是 Traditional robots produce large amounts of electronic waste。or ''e-waste'' 是同位语，解释 electronic waste。which stays in the environment for a long time 是定语从句，修饰 e-waste。', '{"components": [{"text": "Traditional robots", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "produce", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "large amounts of electronic waste", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "or ''e-waste''", "label": "\u540c\u4f4d\u8bed", "type": "unknown", "deletable": false}, {"text": "which stays in the environment for a long time", "label": "\u5b9a\u8bed\u4ece\u53e5", "type": "unknown", "deletable": false}]}', '{"core": ["Traditional robots", "produce", "large amounts of electronic waste"], "modifier": ["or ''e-waste''", "which stays in the environment for a long time"]}', NULL, NULL, 'null', '2025-12-18 08:42:03.077645');
INSERT INTO public.sentence_surgeries (id, version_id, original_sentence, translation, analysis, structure_data, chunks_visual, core_sentence, core_audio_url, coach_script, created_at) VALUES (28, 15, 'In a study published in the journal *Science Advances*, scientists successfully built a robotic arm and a controller using materials from plants and animals.', '在发表在《科学进展》杂志上的一项研究中，科学家们成功地使用植物和动物材料制造了一个机械臂和一个控制器。', '这是一个复合句，主句是 scientists successfully built a robotic arm and a controller。In a study published in the journal *Science Advances* 是地点状语，using materials from plants and animals 是方式状语。', '{"components": [{"text": "In a study published in the journal *Science Advances*", "label": "\u5730\u70b9\u72b6\u8bed", "type": "unknown", "deletable": false}, {"text": "scientists", "label": "\u4e3b\u8bed", "type": "unknown", "deletable": false}, {"text": "successfully built", "label": "\u8c13\u8bed", "type": "unknown", "deletable": false}, {"text": "a robotic arm and a controller", "label": "\u5bbe\u8bed", "type": "unknown", "deletable": false}, {"text": "using materials from plants and animals", "label": "\u65b9\u5f0f\u72b6\u8bed", "type": "unknown", "deletable": false}]}', '{"core": ["scientists", "successfully built", "a robotic arm and a controller"], "modifier": ["In a study published in the journal *Science Advances*", "using materials from plants and animals"]}', NULL, NULL, 'null', '2025-12-18 08:42:03.079694');
